import React, { Component } from 'react';

class EmployeeList extends Component {
    constructor(props){
        super(props);
        this.state = {
            // recordShow: 'false'
            // records : this.props.records,
            // datapass:["krishna", "shyam", "Hari"]
        }
    }
    componentWillUpdate(){
        console.log(this.props.records)
    }

    render() {
        return (
            <div className="listOfEmployee">

                <div className="loeRecords">
                    <table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Phone No.</th>
                                <th>Photo</th>
                            </tr>
                        </thead>
                            <tbody>
                                {

                                    this.props.records.map(record =>
                                    <tr >
                                        <td>{record.name}</td>
                                        <td>{record.email}</td>
                                        <td>{record.phone}</td>
                                        <td>{record.photo}</td>
                                    </tr>
                                )}

                            </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default EmployeeList;
