import React, { Component } from 'react';

class EmployeeList extends Component {
    constructor(props){
        super(props);
        this.state = {
            // recordShow: 'false'
            datapass:["krishna", "shyam", "Hari"]
        }
        this.onClick = this.onClick.bind(this);
    }
    onClick = (e) =>{
        e.preventDefault();
        const records = this.props.records;

        console.log(records);
        // const recordsList = records.map((record, index) =>
        //     <tr>{console.log(record.name)}</tr>
        // )

    }
    render() {
        return (
            <div className="listOfEmployee">
                <div className="loeHeader">
                    <a className="loeDisplayBtn" href="true" onClick={this.onClick}>See all records of employee</a>
                </div>
                <div className="loeRecords">
                    <table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Email</th>
                                <th>Phone No.</th>
                                <th>Photo</th>
                            </tr>
                        </thead>
                            <tbody>
                                {this.state.recordShow}
                                {
                                    this.state.datapass.map((record, index) => {
                                    return <tr key={index}>
                                        <td>{record[index]}</td>
                                    </tr>})
                                }
                            </tbody>
                    </table>
                </div>
            </div>
        );
    }
}

export default EmployeeList;
