import React, { Component } from 'react';

class EmployeForm extends Component {
    constructor(props){
        super(props);
        this.state = {
                name:'',
                email:'',
                phone:'',
                photo:'',
                showData: []
        }
        this.onSubmitHandle  = this.onSubmitHandle.bind(this);
        this.handleChange = this.handleChange.bind(this);

    }
    handleChange = (e) =>{
        e.preventDefault();
        this.setState({[e.target.name] : e.target.value});
        console.log(e);
    }
    onSubmitHandle = (e) =>{
        e.preventDefault();
        this.setState({showData:this.state});
        console.log(this.state.showData);
        // let {name, email, phone, photo} = this.state
        // let newObject={name, email, phone, photo}
        // this.setState(oldState => {showData: oldState.showData.push(newObject)},
        // console.log(this.state.showData))
    }

    render() {
        return (
            <div className="employeeForm">
                <form onSubmit={this.onSubmitHandle}>
                    <div className="form-row">
                        <label>Name</label>
                        <input name="name" type="text" onChange={this.handleChange} />
                    </div>
                    <div className="form-row">
                        <label>Email</label>
                        <input name="email" type="email" onChange={this.handleChange}/>
                    </div>
                    <div className="form-row">
                        <label>Phone no.</label>
                        <input name="phone" type="text" onChange={this.handleChange}/>
                    </div>
                    <div className="form-row">
                        <label>Your photo</label>
                        <input name="photo" type="file" onChange={this.handleChange}/>
                    </div>
                    <div className="form-row">
                        <button onClick={this.handleClick} type="submit">Create record</button>
                    </div>
                </form>
                {/* { this.state.showData &&
                <table>
                    <tr>
                        <td ref="nameElement"></td>
                        <td ref></td>
                        <td></td>
                        <td></td>
                    </tr>
                </table>} */}
            </div>
        );
    }
}

export default EmployeForm;
